import logo from './logo.svg';
import './App.css';
import { Route, NavLink, Switch } from "react-router-dom";

const Header = () => {
  return (
    <ul className="header">
      <li><NavLink exact activeClassName="active" to="/">Home</NavLink></li>
      <li><NavLink activeClassName="active" to="/products">Products</NavLink></li>
      <li><NavLink activeClassName="active" to="/add-book">Add Book</NavLink></li>
      <li><NavLink activeClassName="active" to="/company">Company</NavLink></li>
    </ul>

  );
};

const Home = () => {
  return (
    <h1>Hello! Just testing home!</h1>
  );
};
const Products = (props) => {
  return (
    <h1>Hello! Just testing products! {props.bookFacade.getBooks().map(element => <li>{element.title}</li>)}</h1>
  );
};
const Company = () => {
  return (
    <h1>Hello! Just testing company!</h1>
  );
};

const NoMatch = () => {
  return (
    <h1>Hello! Just testing nomatch!</h1>
  );
};

const AddBook = (props) => {

  const handleChange = (event) => {
    const target = event.target;
    const value = target.type === "checkbox" ? target.checked : target.value;
    const name = target.name;
    setReservation({ ...reservation, [name]: value });
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    props.bookFacade.AddBook(book);
  }

  return (
    <div>
      <h1>Add book</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          id="BookTitle"
          placeholder="Add title" />
        onChange={handleChange}

        <br />
        <input
          type="text"
          id="BookInfo"
          placeholder="Add info" />
        onChange={handleChange}
        <br />
        <br />
        <input
          type="submit"
          value="Save" />
      </form>
    </div>
  );
};

function App(props) {
  return (<div>
    <Header />
    <Switch>
      <Route exact path="/">
        <Home />
      </Route>
      <Route path="/products">
        <Products bookFacade={props.bookFacade} />
      </Route>
      <Route path="/add-book">
        <AddBook bookFacade={props.bookFacade} />
      </Route>
      <Route path="/company">
        <Company />
      </Route>
      <Route>
        <NoMatch />
      </Route>
    </Switch>
  </div>

  );
}

export default App;
